$(document).ready(function(){$("[data-modal=consultation]").on("click",function(){$(".overlay, #consultation").fadeIn("slow")}),$(".modal__close").on("click",function(){$(".overlay, #consultation, #buy-done, #buy").fadeOut("slow")}),$(".button_product-card").each(function(o){$(this).on("click",function(){$("#buy .modal__subtitle").text($(".product-card__title").eq(o).text()),$(".overlay, #buy").fadeIn("slow")})})});