$(document).ready(function(){(new WOW).init(),$(window).scroll(function(){$(this).scrollTop()>1600?$(".to-top").fadeIn():$(".to-top").fadeOut()}),$("a").click(function(){const e=$(this).attr("href");return $("html, body").animate({scrollTop:$(e).offset().top+"px"}),!1});const e=document.querySelector(".advantages"),t=document.querySelectorAll(".advantages img");new IntersectionObserver(function(e,o){e.forEach(e=>{e.isIntersecting&&t.forEach(e=>{"viewed"!==e.className&&e.classList.add("viewed")})})},{threshold:.8}).observe(e)});